<?phprequire('../util.php');$con = openConnection();$uid = $_REQUEST["uid"];$ticket = $_REQUEST["ticket"];$rowCount = $_REQUEST["rowCount"];$pageNr = $_REQUEST["pageNr"];$owner = $_REQUEST["owner"];$filter = "";if($owner!="") 	$filter = "WHERE Owner=".$owner;if(checkSession($con, $uid, $ticket)){	// $filter = $filter." or Owner='".$uid."'";}$result = mysqli_query($con,"SELECT * FROM Vacancies ".$filter." LIMIT ".($pageNr*$rowCount).",".$rowCount);echo("[");$first = true;while($row = mysqli_fetch_array($result)){	if(!$first) echo(","); else $first=false;	echo("{");	writeJsonField($row, "ID", false); echo(",");	writeJsonField($row, "Title", true); echo(",");	writeJsonField($row, "Salary", true); echo(",");	writeJsonField($row, "Organization", true);	echo("}");}echo("]");closeConnection($con);