<?phprequire('../util.php');$con = openConnection();$uid = $_REQUEST["uid"];$ticket = $_REQUEST["ticket"];$data = $_REQUEST["data"];if(!checkSession($con, $uid, $ticket)){	$uid = null;}addRecord($con, "Resumes", array(	"Title"=>getRequestField($data, "post", false),	"Salary"=>getRequestField($data, "salary", false),	"Rubric"=>getRequestField($data, "rubric", true),	"Description"=>getRequestField($data, "skills", false),	"Fio"=>getRequestField($data, "fio", false),	"Age"=>getRequestField($data, "age", false),	"Region"=>getRequestField($data, "region", false),	"Education"=>getRequestField($data, "education", false),	"Experience"=>getRequestField($data, "experience", false),	"Phone"=>getRequestField($data, "phone", false),	"Email"=>getRequestField($data, "email", false),	"Date"=> date('Y-m-d')));echo("{\"success\":true}");closeConnection($con);