<?phprequire('../util.php');$con = openConnection();$uid = $_REQUEST["uid"];$ticket = $_REQUEST["ticket"];$ID = $_REQUEST["id"];$filter = " Owner is NULL";if(checkSession($con, $uid, $ticket)){	$filter = $filter." or Owner='".$uid."'";}$result = mysqli_query($con, "SELECT * FROM Resumes WHERE ID=".$ID);$row = mysqli_fetch_array($result);function writeField($row, $fldName){	$fld = strtolower($fldName);	$val = $row[$fldName];	if(is_string($val)) $val = "'".$val."'";	if(is_null($val)) $val = "null";	echo("\"".$fld."\":".$val);}echo("{");$first = true;foreach(array_keys($row) as $fld){	if(is_string($fld)){		if(!$first) echo(","); else $first=false;		writeField($row, $fld);	}}	echo("}");closeConnection($con);