<?phprequire('../util.php');$con = openConnection();$uid = $_REQUEST["uid"];$ticket = $_REQUEST["ticket"];$rowCount = $_REQUEST["rowCount"];$pageNr = $_REQUEST["pageNr"];$filter = " Owner is NULL";if(chechSession($con, $uid, $ticket)){	$filter = $filter." or Owner='".$uid."'";}$result = mysqli_query($con,"SELECT * FROM Vacancies WHERE ".$filter." LIMIT ".($pageNr*$rowCount).",".$rowCount);echo("[");$first = true;while($row = mysqli_fetch_array($result)){	if(!$first) echo(","); else $first=false;	echo("{\"title\":\"".$row['Title']."\", \"salary\":\"".$row['Salary']."\", \"organization\":\"".$row['Organization']."\"}");}echo("]");closeConnection($con);